use maud::{html, Markup, Render};

pub struct Dropdown<'a> {
    pub trigger: Markup,
    pub menu: Markup,
    pub open: bool,
    pub align_end: bool,
    pub aria_label: &'a str,
}

impl<'a> Render for Dropdown<'a> {
    fn render(&self) -> Markup {
        let class = if self.align_end {
            "sh-dropdown sh-dropdown--end"
        } else {
