//! RSS Feed Generator
//!
//! Generates an Atom/RSS feed for component updates.

use maud::{html, Markup};

/// RSS feed entry
pub struct FeedEntry {
    pub title: String,
    pub link: String,
    pub published: String,
    pub summary: String,
}

/// Generate RSS feed
pub fn generate_rss() -> Markup {
    let entries = vec![
        FeedEntry {
            title: "Shallot v1.0 Released - 129 COMPLETE Components!".to_string(),
            link: "https://shallot.rs/#showcase".to_string(),
            published: "2026-02-24T00:00:00Z".to_string(),
            summary: "All 129 components are now COMPLETE with CSS, tests, and a11y!".to_string(),
        },
        FeedEntry {
            title: "New Component: ZStack".to_string(),
            link: "https://shallot.rs/#layout".to_string(),
            published: "2026-02-23T00:00:00Z".to_string(),
            summary: "Layered overlay layout component with z-index control.".to_string(),
        },
        FeedEntry {
            title: "New Component: CapDrop".to_string(),
            link: "https://shallot.rs/#typography".to_string(),
            published: "2026-02-23T00:00:00Z".to_string(),
            summary: "Decorative drop cap for elegant initial letter styling.".to_string(),
        },
        FeedEntry {
            title: "New Component: LiquidButton".to_string(),
            link: "https://shallot.rs/#animated".to_string(),
            published: "2026-02-23T00:00:00Z".to_string(),
            summary: "SVG filter warp button with liquid animation effect.".to_string(),
        },
        FeedEntry {
            title: "Website Launch".to_string(),
            link: "https://shallot.rs/".to_string(),
            published: "2026-02-22T00:00:00Z".to_string(),
            summary: "Official website launched with interactive component showcase.".to_string(),
        },
    ];

    html! {
        (maud::PreEscaped(r#"<?xml version="1.0" encoding="UTF-8"?>"#))
        feed xmlns="http://www.w3.org/2005/Atom" {
            title { "Shallot Component Library - Updates" }
            link href="https://shallot.rs/" rel="alternate";
            link href="https://shallot.rs/feed.xml" rel="self";
            id { "urn:uuid:shallot-rs" }
            updated { "2026-02-24T00:00:00Z" }
            author {
                name { "Shallot Team" }
            }
            subtitle { "Zero-JS Rust UI Component Library" }

            @for entry in entries {
                entry {
                    title { (entry.title) }
                    link href=(entry.link) rel="alternate";
                    id { (entry.link) }
                    published { (entry.published) }
                    summary { (entry.summary) }
                }
            }
        }
    }
}

/// Generate RSS feed as string
pub fn rss_string() -> String {
    generate_rss().into_string()
}

//! Theme Marketplace
//!
//! Community-submitted theme presets.
//! Users can submit themes via GitHub PRs.

use maud::{html, Markup};

/// Community theme
pub struct CommunityTheme {
    pub name: &'static str,
    pub author: &'static str,
    pub primary: &'static str,
    pub secondary: &'static str,
    pub accent: &'static str,
    pub downloads: u32,
}

/// Community themes
pub const COMMUNITY_THEMES: &[CommunityTheme] = &[
    CommunityTheme {
        name: "Cyberpunk",
        author: "@rustacean",
        primary: "#fcee0a",
        secondary: "#00f0ff",
        accent: "#ff003c",
        downloads: 1247,
    },
    CommunityTheme {
        name: "Synthwave",
        author: "@neon_dev",
        primary: "#ff71ce",
        secondary: "#01cdfe",
        accent: "#05ffa1",
        downloads: 892,
    },
    CommunityTheme {
        name: "Nordic",
        author: "@viking_coder",
        primary: "#5e81ac",
        secondary: "#81a1c1",
        accent: "#88c0d0",
        downloads: 654,
    },
    CommunityTheme {
        name: "Solarized",
        author: "@ethan_schoonover",
        primary: "#268bd2",
        secondary: "#2aa198",
        accent: "#cb4b16",
        downloads: 1523,
    },
    CommunityTheme {
        name: "Gruvbox",
        author: "@morhetz",
        primary: "#d79921",
        secondary: "#689d6a",
        accent: "#cc241d",
        downloads: 987,
    },
    CommunityTheme {
        name: "Dracula",
        author: "@dracula_theme",
        primary: "#bd93f9",
        secondary: "#50fa7b",
        accent: "#ff79c6",
        downloads: 2341,
    },
];

/// Render theme marketplace
pub fn render() -> Markup {
    html! {
        section class="sh-theme-marketplace" id="community-themes" {
            div class="sh-container" {
                h2 class="sh-theme-marketplace__title" {
                    "🎨 Community Themes"
                }
                p class="sh-theme-marketplace__description" {
                    "Themes submitted by the Shallot community. "
                    a href="https://github.com/shallot-rs/shallot/themes" { "Submit your own!" }
                }

                div class="sh-theme-grid" {
                    @for theme in COMMUNITY_THEMES {
                        article class="sh-theme-card" {
                            div
                                class="sh-theme-card__preview"
                                style=(format!("background: linear-gradient(135deg, {} 0%, {} 50%, {} 100%)", theme.primary, theme.secondary, theme.accent))
                            {}
                            div class="sh-theme-card__content" {
                                h3 class="sh-theme-card__name" {
                                    (theme.name)
                                }
                                p class="sh-theme-card__author" {
                                    "by " (theme.author)
                                }
                                div class="sh-theme-card__stats" {
                                    span class="sh-theme-card__downloads" {
                                        "📥 " (theme.downloads)
                                    }
                                }
                                button
                                    class="sh-theme-card__install"
                                    type="button"
                                    data-primary=(theme.primary)
                                    data-secondary=(theme.secondary)
                                    data-accent=(theme.accent)
                                {
                                    "Use Theme"
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

/// Generate theme marketplace CSS
pub fn theme_marketplace_css() -> String {
    r#"
/* Theme Marketplace */
.sh-theme-marketplace {
    padding: 4rem 0;
    background: var(--sh-surface-2);
}

.sh-theme-marketplace__title {
    font-size: 1.75rem;
    text-align: center;
    margin-bottom: 0.5rem;
}

.sh-theme-marketplace__description {
    text-align: center;
    color: var(--sh-text-secondary);
    margin-bottom: 2rem;
}

.sh-theme-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(16rem, 1fr));
    gap: 1.5rem;
}

.sh-theme-card {
    background: var(--sh-surface);
    border: 1px solid var(--sh-border);
    border-radius: var(--sh-radius-lg);
    overflow: hidden;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.sh-theme-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
}

.sh-theme-card__preview {
    height: 6rem;
}

.sh-theme-card__content {
    padding: 1rem;
}

.sh-theme-card__name {
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
}

.sh-theme-card__author {
    font-size: 0.75rem;
    color: var(--sh-text-muted);
    margin-bottom: 0.5rem;
}

.sh-theme-card__stats {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 0.75rem;
}

.sh-theme-card__downloads {
    font-size: 0.75rem;
    color: var(--sh-text-muted);
}

.sh-theme-card__install {
    width: 100%;
    padding: 0.5rem;
    background: var(--sh-primary);
    color: white;
    border: none;
    border-radius: var(--sh-radius-md);
    font-size: 0.75rem;
    font-weight: 600;
    cursor: pointer;
    transition: background 0.2s ease;
}

.sh-theme-card__install:hover {
    background: var(--sh-primary-hover);
}
"#
    .to_string()
}

//! Webring Widget
//!
//! A CSS-only webring navigation component.
//! Joins the "No-JS Webring" for 90s web authenticity.

use maud::{html, Markup};

/// Webring navigation
pub fn render() -> Markup {
    html! {
        div class="sh-webring" role="navigation" aria-label="Webring navigation" {
            h3 class="sh-webring__title" {
                "🌐 No-JS Webring"
            }
            p class="sh-webring__description" {
                "A collection of websites built without JavaScript"
            }
            div class="sh-webring__nav" {
                a href="#" class="sh-webring__link" aria-label="Previous site in webring" {
                    span aria-hidden="true" { "←" }
                    " Prev"
                }
                a href="#" class="sh-webring__link sh-webring__link--random" aria-label="Random site" {
                    "🎲 Random"
                }
                a href="#" class="sh-webring__link" aria-label="Next site in webring" {
                    "Next "
                    span aria-hidden="true" { "→" }
                }
            }
            p class="sh-webring__join" {
                "Want to join? "
                a href="https://github.com/no-js-webring" { "Apply here" }
            }
        }
    }
}

/// Generate webring CSS
pub fn webring_css() -> String {
    r#"
/* Webring Widget */
.sh-webring {
    max-width: 24rem;
    margin: 2rem auto;
    padding: 1.5rem;
    background: var(--sh-surface-2);
    border: 2px dashed var(--sh-border);
    border-radius: var(--sh-radius-lg);
    text-align: center;
}

.sh-webring__title {
    font-size: 1rem;
    font-weight: 600;
    color: var(--sh-text);
    margin-bottom: 0.5rem;
}

.sh-webring__description {
    font-size: 0.75rem;
    color: var(--sh-text-muted);
    margin-bottom: 1rem;
}

.sh-webring__nav {
    display: flex;
    justify-content: center;
    gap: 0.5rem;
    margin-bottom: 1rem;
}

.sh-webring__link {
    display: inline-flex;
    align-items: center;
    gap: 0.25rem;
    padding: 0.5rem 0.75rem;
    background: var(--sh-surface);
    border: 1px solid var(--sh-border);
    border-radius: var(--sh-radius-md);
    color: var(--sh-text-secondary);
    text-decoration: none;
    font-size: 0.75rem;
    font-weight: 500;
    transition: all 0.2s ease;
}

.sh-webring__link:hover {
    background: var(--sh-primary);
    border-color: var(--sh-primary);
    color: white;
}

.sh-webring__link--random {
    background: var(--sh-surface-2);
}

.sh-webring__join {
    font-size: 0.75rem;
    color: var(--sh-text-muted);
}

.sh-webring__join a {
    color: var(--sh-primary);
    text-decoration: underline;
}
"#
    .to_string()
}

